import React from 'react';
import data from '../data.json';
import '../App.css';
import './titleSection.css';
import { SocialIcon } from 'react-social-icons';


class TitlesSection extends React.Component {
    state = {
        color: 'white'
    }
    changeColor = () => {
        debugger
        if(h =='h1'){
            this.setState({
                color: this.state.color === 'white' ? 'pink' : 'white'
            })
        }
   
      }
    render() {
        return (
            <div className="fullPage first">
                <h1
                    style={{ color: this.state.color, cursor: 'pointer' }}
                    onMouseOver={() => { this.changeColor('h1') }}
                    onMouseLeave={() => { this.changeColor('h1') }}>
                    {data.title}
                    </h1>
                <div>
                    <h2
                    style={{ color: this.state.color, cursor: 'pointer' }}
                    onMouseOver={() => { this.changeColor('h2') }}
                        onMouseLeave={() => { this.changeColor('h2') }} >
                            {data.subtitle}
                    </h2>
                </div>
                <div className="icons-wrapper">
                    {Object.keys(data.links).map(key => {
                        return (
                            <div className="icon">
                                <SocialIcon url={data.links[key]} />
                            </div>
                        )
                    })}
                </div>
            </div>
        )
    }
}

export default TitlesSection;
